import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [link, setLink] = useState('');
  const [uploadChoice, setUploadChoice] = useState('document');
  const [documentFile, setDocumentFile] = useState(null);
  const [linkPhoto, setLinkPhoto] = useState(null);
  const [uploadedItem, setUploadedItem] = useState(null);

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleUploadChoiceChange = (choice) => {
    setUploadChoice(choice);
  };

  const handleDocumentFileChange = (e) => {
    setDocumentFile(e.target.files[0]);
  };

  const handleLinkPhotoChange = (e) => {
    setLinkPhoto(e.target.files[0]);
  };

  const handleUpload = () => {
    if (uploadChoice === 'document' && title && author && documentFile && linkPhoto) {
      const uploadedDocument = {
        type: 'document',
        title,
        author,
        documentFile, // Store the selected document file
        linkPhoto,    // Store the selected link photo
      };
      setUploadedItem(uploadedDocument);
    } else if (uploadChoice === 'link' && link && linkPhoto) {
      const uploadedLink = {
        type: 'link',
        link,
        linkPhoto,    // Store the selected link photo
      };
      setUploadedItem(uploadedLink);
    } else {
      alert('Please fill in all required fields.');
    }

    closeModal();
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document or Link</h2>
        <div>
          <label>Select Upload Type:</label>
          <br />
          <input
            type="radio"
            id="document"
            name="uploadType"
            value="document"
            checked={uploadChoice === 'document'}
            onChange={() => handleUploadChoiceChange('document')}
          />
          <label htmlFor="document">Document</label>
          <br />
          <input
            type="radio"
            id="link"
            name="uploadType"
            value="link"
            checked={uploadChoice === 'link'}
            onChange={() => handleUploadChoiceChange('link')}
          />
          <label htmlFor="link">Link</label>
        </div>

        {uploadChoice === 'document' ? (
          <div>
            <label>Title:</label>
            <input type="text" value={title} onChange={handleTitleChange} />
            <br />
            <label>Author:</label>
            <input type="text" value={author} onChange={handleAuthorChange} />
            <br />
            <label>Choose Document:</label>
            <input type="file" onChange={handleDocumentFileChange} />
            <br />
            <label>Choose Photo for Document:</label>
            <input type="file" onChange={handleLinkPhotoChange} />
          </div>
        ) : (
          <div>
            <label>Link:</label>
            <input type="text" value={link} onChange={handleLinkChange} />
            <br />
            <label>Choose Photo for Link:</label>
            <input type="file" onChange={handleLinkPhotoChange} />
          </div>
        )}
        {/* Display uploaded item */}
        {uploadedItem && (
          <div>
            <h3>Uploaded Item:</h3>
            {uploadedItem.type === 'document' ? (
              <div>
                <p>Title: {uploadedItem.title}</p>
                <p>Author: {uploadedItem.author}</p>
                <p>Type: Document</p>
                {/* Display the document file */}
                <p>Document File: {uploadedItem.documentFile.name}</p>
                {/* Display the link photo */}
                <p>Link Photo: {uploadedItem.linkPhoto.name}</p>
                <p>Download Link: {/* Add the download link for the document */}</p>
              </div>
            ) : (
              <div>
                <p>Link: {uploadedItem.link}</p>
                <p>Type: Link</p>
                {/* Display the link photo */}
                <p>Link Photo: {uploadedItem.linkPhoto.name}</p>
              </div>
            )}
          </div>
        )}
      </Modal>
    </div>
  );
};

export default SubjectPage;
