import React, { useState } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { faculties } from './data';

const Container = styled.div`
  display: flex;
  flex-direction: row;
  align-items: flex-start; /* Align items at the top */
  margin: 0 20px;
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const SubjectButtonContainer = styled.div`
  margin-top: 10px; /* Add margin to separate subject buttons vertically */
`;

const SubjectButton = styled(Link)`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;
  text-decoration: none;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const FacultyButtons = ({ setSelectedFaculty, setSelectedSubject, selectedFaculty }) => {
  const handleFacultyClick = (facultyId) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
    setSelectedSubject(null);
  };

  const handleSubjectClick = (subjectName) => {
    setSelectedSubject(subjectName);
  };

  return (
    <Container>
      {faculties.map((faculty) => (
        <div key={faculty.id}>
          <FacultyButton onClick={() => handleFacultyClick(faculty.id)}>
            {faculty.name}
          </FacultyButton>
          {selectedFaculty !== null && faculty.id === selectedFaculty && (
            <div>
              {faculty.subjects.map((subject) => (
                <SubjectButtonContainer key={subject.id}>
                  <SubjectButton
                    to={`/subjects/${subject.name}`}
                    onClick={() => handleSubjectClick(subject.name)}
                  >
                    {subject.name}
                  </SubjectButton>
                </SubjectButtonContainer>
              ))}
            </div>
          )}
        </div>
      ))}
    </Container>
  );
};

export default FacultyButtons;
