import React, { useState } from 'react';
import styled from 'styled-components';

const ButtonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  margin-top: 1rem;
`;

const FacultyButton = styled.button`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  background-color: #333;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const SubjectButton = styled.button`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  background-color: #333;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const FacultyButtons = () => {
  const [showButtons, setShowButtons] = useState(false);

  const handleFacultyClick = () => {
    setShowButtons(!showButtons);
  };

  return (
    <div>
      <FacultyButton onClick={handleFacultyClick}>Faculty 1</FacultyButton>


      {showButtons && (
        <ButtonContainer>
          {/* Replace the placeholder buttons with your actual subjects */}
          <SubjectButton>OOP</SubjectButton>
          <SubjectButton>OS</SubjectButton>
          <SubjectButton>Web development</SubjectButton>
          {/* Add more subject buttons as needed */}
        </ButtonContainer>
      )}
    </div>
  );
};

export default FacultyButtons;
