import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';
import ErrorMessage from './ErrorMessage';

const SubjectPage = () => {
  // ... (your existing state and functions)

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document or Link</h2>
        {error && <ErrorMessage message={error} />}
        {successMessage && (
          <div
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              padding: '12px 20px',
              margin: '10px 0',
              borderRadius: '6px',
            }}
          >
            {successMessage}
          </div>
        )}
        <div>
          <label>Select Upload Type:</label>
          <br />
          <input
            type="radio"
            id="document"
            name="uploadType"
            value="document"
            checked={uploadChoice === 'document'}
            onChange={() => handleUploadChoiceChange('document')}
          />
          <label htmlFor="document">Document</label>
          <br />
          <input
            type="radio"
            id="link"
            name="uploadType"
            value="link"
            checked={uploadChoice === 'link'}
            onChange={() => handleUploadChoiceChange('link')}
          />
          <label htmlFor="link">Link</label>
        </div>

        {uploadChoice === 'document' ? (
          <div>
            <label>Title:</label>
            <input type="text" value={title} onChange={handleTitleChange} />
            <br />
            <label>Author:</label>
            <input type="text" value={author} onChange={handleAuthorChange} />
            <br />
            <label>Choose Document:</label>
            <input
              type="file"
              accept=".pdf,.doc,.docx,.txt"
              onChange={(e) => handleDocumentFileChange(e.target.files[0])}
            />
            <br />
            <label>Choose Photo for Document:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleLinkPhotoChange(e.target.files[0])}
            />
          </div>
        ) : (
          <div>
            <label>Link:</label>
            <input type="text" value={link} onChange={handleLinkChange} />
            <br />
            <label>Choose Photo for Link:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleLinkPhotoChange(e.target.files[0])}
            />
          </div>
        )}

        <button
          onClick={handleUpload}
          style={{
            backgroundColor: '#8b0000',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            padding: '0.6rem 1.2rem',
            fontSize: '1rem',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Upload
        </button>
      </Modal>

      <div>
        {uploadedDocuments.length > 0 && (
          <div>
            <h2>Uploaded Documents and Links</h2>
            <ul>
              {uploadedDocuments.map((item, index) => (
                <li key={index}>
                  {item.title && <div>Title: {item.title}</div>}
                  {item.author && <div>Author: {item.author}</div>}
                  {item.link && <div>Link: {item.link}</div>}
                  {item.file && (
                    <div>
                      <a
                        href={URL.createObjectURL(item.file)}
                        target="_blank"
                        rel="noopener noreferrer"
                      >
                        View Document
                      </a>
                    </div>
                  )}
                  {item.photo && (
                    <div>
                      <img src={URL.createObjectURL(item.photo)} alt="Uploaded" />
                    </div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default SubjectPage;
