import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const SubjectButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  margin-bottom: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const SearchBar = styled.input`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  border: 1px solid #8b0000; /* Dark red color */
  border-radius: 4px;
  margin-left: 20px;
`;

const FacultyButtons = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [searchText, setSearchText] = useState('');

  const faculties = [
    { id: 1, name: 'Faculty 1' },
    { id: 2, name: 'Faculty 2' },
    { id: 3, name: 'Faculty 3' },
  ];

  const subjects = [
    { id: 1, facultyId: 1, name: 'Subject 1' },
    { id: 2, facultyId: 1, name: 'Subject 2' },
    { id: 3, facultyId: 2, name: 'Subject 3' },
    { id: 4, facultyId: 2, name: 'Subject 4' },
    { id: 5, facultyId: 3, name: 'Subject 5' },
    { id: 6, facultyId: 3, name: 'Subject 6' },
  ];

  const handleFacultyClick = (facultyId) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
  };

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  const filteredSubjects = subjects.filter((subject) => {
    if (!selectedFaculty) return true; // Show all subjects if no faculty is selected
    return subject.facultyId === selectedFaculty;
  });

  return (
    <Container>
      {faculties.map((faculty) => (
        <div key={faculty.id}>
          <FacultyButton onClick={() => handleFacultyClick(faculty.id)}>
            {faculty.name}
          </FacultyButton>
          {selectedFaculty === faculty.id && (
            <div>
              <SearchBar
                value={searchText}
                onChange={handleSearchChange}
                placeholder={`Search in ${faculty.name}`}
              />
              {filteredSubjects
                .filter((subject) =>
                  subject.name.toLowerCase().includes(searchText.toLowerCase())
                )
                .map((subject) => (
                  <SubjectButton key={subject.id}>{subject.name}</SubjectButton>
                ))}
            </div>
          )}
        </div>
      ))}
    </Container>
  );
};

export default FacultyButtons;
