import React from 'react';
import styled from 'styled-components';

const HeaderContainer = styled.header`
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  padding: 1rem;
`;


const LogoContainer = styled.div`
  display: flex;
  align-items: center;
`;

const Logo = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  margin-right: 1rem;
`;

const SearchBar = styled.input`
  padding: 0.5rem;
  font-size: 1rem;
  border: none;
  border-radius: 5px;
  background-color: #6a0000; /* Slightly darker shade of red for the search bar */
  color: #fff;
  ::placeholder {
    color: #fff;
  }
`;

const AuthButtons = styled.div`
  display: flex;
  align-items: center;
`;

const AuthInput = styled.input`
  padding: 0.5rem;
  font-size: 1rem;
  border: none;
  border-radius: 5px;
  background-color: #6a0000; /* Slightly darker shade of red for the text boxes */
  color: #fff;
  margin-left: 0.5rem;
  ::placeholder {
    color: #fff;
  }
`;

const AuthButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-left: 0.5rem;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const Header = ({ selectedFacultyName, onSearchChange }) => {
  return (
    <HeaderContainer>
      <Logo>Logo</Logo>
      <SearchBar
        onChange={onSearchChange}
        placeholder={`Search in ${selectedFacultyName}`}
      />
    </HeaderContainer>
  );
};

export default Header;
