import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin-bottom: 20px; /* Add margin between the FacultyButtons component and the content below */
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const FacultyButtons = ({ selectedFaculty, setSelectedFaculty }) => {
  const faculties = [
    { id: 1, name: 'Faculty 1' },
    { id: 2, name: 'Faculty 2' },
    { id: 3, name: 'Faculty 3' },
  ];

  const handleFacultyClick = (facultyId) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
  };

  return (
    <Container>
      {faculties.map((faculty) => (
        <FacultyButton
          key={faculty.id}
          onClick={() => handleFacultyClick(faculty.id)}
          style={{ backgroundColor: selectedFaculty === faculty.id ? '#6a0000' : '#8b0000' }}
        >
          {faculty.name}
        </FacultyButton>
      ))}
    </Container>
  );
};

export default FacultyButtons;
