import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';
import ErrorMessage from './ErrorMessage'; // Import the ErrorMessage component

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [link, setLink] = useState('');
  const [uploadChoice, setUploadChoice] = useState('document');
  const [documentFile, setDocumentFile] = useState(null);
  const [linkPhoto, setLinkPhoto] = useState(null);
  const [error, setError] = useState(null); // State to hold error message
  const [successMessage, setSuccessMessage] = useState(null); // State for success message
  const [uploadedDocuments, setUploadedDocuments] = useState([]); // State to store uploaded documents

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setError(null); // Clear any previous error messages when closing the modal
  };

  const handleUpload = () => {
    if (uploadChoice === 'document' && title && author && documentFile && linkPhoto) {
      // Simulate storing the uploaded document
      const uploadedDocument = {
        title,
        author,
        file: documentFile,
        photo: linkPhoto,
      };

      // Add the uploaded document to the list
      setUploadedDocuments([...uploadedDocuments, uploadedDocument]);

      // Reset form fields
      setTitle('');
      setAuthor('');
      setDocumentFile(null);
      setLinkPhoto(null);

      // Display success message
      setSuccessMessage('Document uploaded successfully.');

      // Close the modal
      setIsModalOpen(false);
    } else if (uploadChoice === 'link' && link && linkPhoto) {
      // Simulate storing the uploaded link
      const uploadedLink = {
        link,
        photo: linkPhoto,
      };

      // Add the uploaded link to the list
      setUploadedDocuments([...uploadedDocuments, uploadedLink]);

      // Reset form fields
      setLink('');
      setLinkPhoto(null);

      // Display success message
      setSuccessMessage('Link uploaded successfully.');

      // Close the modal
      setIsModalOpen(false);
    } else {
      // Display an error message if any required fields are empty
      setError('Please fill in all required fields.');
    }
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document or Link</h2>
        {error && <ErrorMessage message={error} />}
        {successMessage && (
          <div
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              padding: '12px 20px',
              margin: '10px 0',
              borderRadius: '6px',
            }}
          >
            {successMessage}
          </div>
        )}
        <div>
          <label>Select Upload Type:</label>
          <br />
          <input
            type="radio"
            id="document"
            name="uploadType"
            value="document"
            checked={uploadChoice === 'document'}
            onChange={() => handleUploadChoiceChange('document')}
          />
          <label htmlFor="document">Document</label>
          <br />
          <input
            type="radio"
            id="link"
            name="uploadType"
            value="link"
            checked={uploadChoice === 'link'}
            onChange={() => handleUploadChoiceChange('link')}
          />
          <label htmlFor="link">Link</label>
        </div>

        {uploadChoice === 'document' ? (
          <div>
            <label>Title:</label>
            <input type="text" value={title} onChange={handleTitleChange} />
            <br />
            <label>Author:</label>
            <input type="text" value={author} onChange={handleAuthorChange} />
            <br />
            <label>Choose Document:</label>
            <input
              type="file"
              accept=".pdf,.doc,.docx,.txt"
              onChange={(e) => handleDocumentFileChange(e)}
            />
            <br />
            <label>Choose Photo for Document:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleLinkPhotoChange(e)}
            />
          </div>
        ) : (
          <div>
            <label>Link:</label>
            <input type="text" value={link} onChange={handleLinkChange} />
            <br />
            <label>Choose Photo for Link:</label>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => handleLinkPhotoChange(e)}
            />
          </div>
        )}

        <button
          onClick={handleUpload}
          style={{
            backgroundColor: '#8b0000',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            padding: '0.6rem 1.2rem',
            fontSize: '1rem',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Upload
        </button>
      </Modal>
    </div>
  );
};

export default SubjectPage;
