import React from 'react';
import styled from 'styled-components';

const HeaderContainer = styled.div`
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  padding: 1rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const LogoContainer = styled.div`
  display: flex;
  align-items: center;
`;

const Logo = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  margin-right: 1rem;
`;

const SearchBar = styled.input`
  padding: 0.5rem;
  font-size: 1rem;
  border: none;
  border-radius: 5px;
  background-color: #6a0000; /* Slightly darker shade of red for the search bar */
  color: #fff;
  ::placeholder {
    color: #fff;
  }
`;

const FacultyButtons = styled.div`
  display: flex;
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-left: 0.5rem;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const Header = () => {
  return (
    <HeaderContainer>
      <LogoContainer>
        <Logo>AAAA</Logo> {/* Replace with your logo */}
        <SearchBar type="text" placeholder="Search..." />
      </LogoContainer>
      <FacultyButtons>
        <FacultyButton>Faculty 1</FacultyButton>
        <FacultyButton>Faculty 2</FacultyButton>
        <FacultyButton>Faculty 3</FacultyButton>
      </FacultyButtons>
    </HeaderContainer>
  );
};

export default Header;
