import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const LeftContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 200px;
`;

const FacultyButtonContainer = styled.div`
  display: flex;
  flex-direction: row; /* Updated to row */
  align-items: center; /* Align buttons to the center */
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-right: 10px; /* Add margin to separate buttons */
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const RightContainer = styled.div`
  display: flex;
  flex-direction: column; /* Updated to column */
  align-items: flex-start; /* Align buttons to the left */
  margin-left: 20px;
`;

const SubjectButtonContainer = styled.div`
  display: flex;
  flex-direction: row; /* Display subject buttons horizontally */
`;

const SubjectButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px; /* Add margin to separate buttons */
  margin-bottom: 10px; /* Add margin at the bottom */
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const FacultyButtons = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [showSubjects, setShowSubjects] = useState({});

  const faculties = [
    { id: 1, name: 'Faculty 1' },
    { id: 2, name: 'Faculty 2' },
    { id: 3, name: 'Faculty 3' },
  ];

  const subjects = [
    { id: 1, facultyId: 1, name: 'Subject 1' },
    { id: 2, facultyId: 1, name: 'Subject 2' },
    { id: 3, facultyId: 2, name: 'Subject 3' },
    { id: 4, facultyId: 2, name: 'Subject 4' },
    { id: 5, facultyId: 3, name: 'Subject 5' },
    { id: 6, facultyId: 3, name: 'Subject 6' },
  ];

  const handleFacultyClick = (facultyId) => {
    if (selectedFaculty === facultyId) {
      setSelectedFaculty(null);
    } else {
      setSelectedFaculty(facultyId);
    }
    setShowSubjects((prevShowSubjects) => ({
      ...prevShowSubjects,
      [facultyId]: !prevShowSubjects[facultyId],
    }));
  };

  return (
    <Container>
      <LeftContainer>
        <FacultyButtonContainer>
          {faculties.map((faculty) => (
            <FacultyButton key={faculty.id} onClick={() => handleFacultyClick(faculty.id)}>
              {faculty.name}
            </FacultyButton>
          ))}
        </FacultyButtonContainer>
      </LeftContainer>
      <RightContainer>
        {selectedFaculty &&
          <SubjectButtonContainer>
            {subjects
              .filter((subject) => subject.facultyId === selectedFaculty)
              .map((subject) => (
                <SubjectButton key={subject.id}>{subject.name}</SubjectButton>
              ))}
          </SubjectButtonContainer>
        }
      </RightContainer>
    </Container>
  );
};

export default FacultyButtons;
