import React, { useState } from 'react';
import styled from 'styled-components'; // Import styled-components if you use it in your app

const StyledButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-top: 10px; /* Add margin to match your design */

  &:hover {
    background-color: #6a0000;
  }
`;

const ChooseFileButton = styled.label`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  transition: background-color 0.2s;
  display: inline-block;

  &:hover {
    background-color: #6a0000;
  }
`;

function FileUpload() {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = () => {
    // Implement the upload logic here
    if (selectedFile) {
      // You can use the selectedFile for the upload
      alert('File uploaded successfully.');
    }
  };

  return (
    <div>
      <h2>Upload a File</h2>
      <ChooseFileButton>
        Choose File
        <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
      </ChooseFileButton>
      <StyledButton onClick={handleUpload}>Upload</StyledButton>
    </div>
  );
}

export default FileUpload;
