import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';
import ErrorMessage from './ErrorMessage';

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [link, setLink] = useState('');
  const [uploadChoice, setUploadChoice] = useState('document');
  const [documentFile, setDocumentFile] = useState(null);
  const [linkPhoto, setLinkPhoto] = useState(null);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState(null); // State for success message
  const [uploadedDocuments, setUploadedDocuments] = useState([]); // State to store uploaded documents

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setError(null);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleUploadChoiceChange = (choice) => {
    setUploadChoice(choice);
  };

  const handleDocumentFileChange = (e) => {
    setDocumentFile(e.target.files[0]);
  };

  const handleLinkPhotoChange = (e) => {
    setLinkPhoto(e.target.files[0]);
  };

  const handleUpload = () => {
    if (uploadChoice === 'document' && title && author && documentFile && linkPhoto) {
      // Simulate storing the uploaded document
      const uploadedDocument = {
        title,
        author,
        file: documentFile,
        photo: linkPhoto,
      };

      // Add the uploaded document to the list
      setUploadedDocuments([...uploadedDocuments, uploadedDocument]);

      // Reset form fields
      setTitle('');
      setAuthor('');
      setDocumentFile(null);
      setLinkPhoto(null);

      // Display success message
      setSuccessMessage('Document uploaded successfully.');

      // Close the modal
      setIsModalOpen(false);
    } else if (uploadChoice === 'link' && link && linkPhoto) {
      // Simulate storing the uploaded link
      const uploadedLink = {
        link,
        photo: linkPhoto,
      };

      // Add the uploaded link to the list
      setUploadedDocuments([...uploadedDocuments, uploadedLink]);

      // Reset form fields
      setLink('');
      setLinkPhoto(null);

      // Display success message
      setSuccessMessage('Link uploaded successfully.');

      // Close the modal
      setIsModalOpen(false);
    } else {
      setError('Please fill in all required fields.');
    }
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document or Link</h2>
        {error && <ErrorMessage message={error} />}
        {successMessage && (
          <div
            style={{
              backgroundColor: '#4CAF50',
              color: 'white',
              padding: '12px 20px',
              margin: '10px 0',
              borderRadius: '6px',
            }}
          >
            {successMessage}
          </div>
        )}
        <div>
          <label>Select Upload Type:</label>
          <br />
          {/* ... (rest of the code remains the same) */}
        </div>

        <button
          onClick={handleUpload}
          style={{
            backgroundColor: '#8b0000',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            padding: '0.6rem 1.2rem',
            fontSize: '1rem',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Upload
        </button>
      </Modal>

      {/* Display uploaded documents */}
      {uploadedDocuments.length > 0 && (
        <div>
          <h2>Uploaded Documents and Links</h2>
          <ul>
            {uploadedDocuments.map((item, index) => (
              <li key={index}>
                {item.title && <div>Title: {item.title}</div>}
                {item.author && <div>Author: {item.author}</div>}
                {item.link && <div>Link: {item.link}</div>}
                {item.file && (
                  <div>
                    <a href={URL.createObjectURL(item.file)} download>
                      Download Document
                    </a>
                  </div>
                )}
                {item.photo && (
                  <div>
                    <img src={URL.createObjectURL(item.photo)} alt="Uploaded" />
                  </div>
                )}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SubjectPage;
