import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
`;

const LeftContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 200px;
`;

const FacultyButtonContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
`;

const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-bottom: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const RightContainer = styled.div`
  display: flex;
  flex-direction: row; /* Updated to row */
  align-items: center;
  margin-left: 20px;
`;

const SubjectButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  margin-bottom: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;



const FacultyButtons = () => {

  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [selectedFacultyName, setSelectedFacultyName] = useState(null);
  const [selectedSubjectName, setSelectedSubjectName] = useState(null);
  const [showSubjects, setShowSubjects] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFacultyClick = (faculty) => {
    if (selectedFaculty === faculty) {
      // If the same faculty button is clicked again, hide the subjects
      setShowSubjects(false);
      setSelectedSubjectName(null);
    } else {
      // If a different faculty button is clicked, show the subjects
      setShowSubjects(true);
    }

    setSelectedFaculty(faculty);
    setSelectedFacultyName(faculty);
  };

  // Dummy subject data based on faculty selection
  const subjects = {
    'Faculty 1': ['Subject 1A', 'Subject 1B', 'Subject 1C'],
    'Faculty 2': ['Subject 2A', 'Subject 2B', 'Subject 2C'],
    'Faculty 3': ['Subject 3A', 'Subject 3B', 'Subject 3C'],
  };

  const handleSubjectClick = (subject) => {
    setSelectedSubjectName(subject);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  return (
    <Container>
      <LeftContainer>
        <h2>Faculties</h2>
        <FacultyButtonContainer>
          <FacultyButton onClick={() => handleFacultyClick('Faculty 1')}>Faculty 1</FacultyButton>
          <FacultyButton onClick={() => handleFacultyClick('Faculty 2')}>Faculty 2</FacultyButton>
          <FacultyButton onClick={() => handleFacultyClick('Faculty 3')}>Faculty 3</FacultyButton>
        </FacultyButtonContainer>
      </LeftContainer>

      {showSubjects && (
        <RightContainer>
          <h3>Subjects for {selectedFacultyName}</h3>
          <SubjectTree>
            {subjects[selectedFaculty].map((subject) => (
              <SubjectNode key={subject}>
                <SubjectButton onClick={() => handleSubjectClick(subject)}>
                  {subject}
                </SubjectButton>
              </SubjectNode>
            ))}
          </SubjectTree>
          {selectedSubjectName && (
            <>
              {selectedFile ? (
                <p>File selected: {selectedFile.name}</p>
              ) : (
                <>
                  <input
                    type="file"
                    style={{ display: 'none' }}
                    id="file-upload"
                    onChange={handleFileUpload}
                  />
                  <FileUploadButton htmlFor="file-upload">Upload File</FileUploadButton>
                </>
              )}
            </>
          )}
        </RightContainer>
      )}
    </Container>
  );
};

export default FacultyButtons;
