import React, { useState } from 'react';
import styled from 'styled-components';
import FacultyButtons from './FacultyButtons';
import FileUpload from './FileUpload';

const Container = styled.div`
  text-align: center;
  padding: 100px;
`;

const WelcomeText = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 20px;
`;

const SubText = styled.p`
  font-size: 1.2rem;
  margin-bottom: 40px;
`;

const UploadSection = styled.div`
  margin-top: 20px;
`;

const ChooseFileButton = styled.label`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;
  display: inline-block;

  &:hover {
    background-color: #6a0000;
  }
`;

const WelcomingPage = () => {
  const [selectedSubject, setSelectedSubject] = useState(null);

  // Function to handle subject selection
  const handleSubjectSelection = (subjectName) => {
    setSelectedSubject(subjectName);
  };

  return (
    <Container>
      <WelcomeText>Welcome to our FDRS!</WelcomeText>
      <SubText>Explore our faculties and subjects</SubText>
      <FacultyButtons onSubjectSelect={handleSubjectSelection} />
      {selectedSubject && (
        <UploadSection>
          <h2>Upload Files for {selectedSubject}</h2>
          <ChooseFileButton>
            Choose File
            <input type="file" onChange={handleFileChange} style={{ display: 'none' }} />
          </ChooseFileButton>
          <FileUpload />
        </UploadSection>
      )}
    </Container>
  );
};

export default WelcomingPage;
