import React, { useState } from 'react';
import styled from 'styled-components';

const Container = styled.div`
  display: flex;
  flex-direction: row;
`;

const LeftContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  width: 200px;
`;

const RightContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  margin-left: 20px;
`;

const FacultyTree = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const FacultyNode = styled.li`
  margin-left: 10px;
`;

const FacultyButton = styled.button`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  background-color: #333;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const SubjectTree = styled.ul`
  list-style-type: none;
  padding: 0;
  margin: 0;
`;

const SubjectNode = styled.li`
  margin-left: 10px;
`;

const SubjectButton = styled.button`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  background-color: #333;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const FileUploadButton = styled.label`
  padding: 0.5rem 1rem;
  font-size: 1rem;
  background-color: #007bff;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;
`;

const FacultyButtons = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [selectedFacultyName, setSelectedFacultyName] = useState(null);
  const [selectedSubjectName, setSelectedSubjectName] = useState(null);
  const [showSubjects, setShowSubjects] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFacultyClick = (faculty) => {
    if (selectedFaculty === faculty) {
      // If the same faculty button is clicked again, hide the subjects
      setShowSubjects(false);
      setSelectedSubjectName(null);
    } else {
      // If a different faculty button is clicked, show the subjects
      setShowSubjects(true);
    }

    setSelectedFaculty(faculty);
    setSelectedFacultyName(faculty);
  };

  // Dummy subject data based on faculty selection
  const subjects = {
    'Faculty 1': ['Subject 1A', 'Subject 1B', 'Subject 1C'],
    'Faculty 2': ['Subject 2A', 'Subject 2B', 'Subject 2C'],
    'Faculty 3': ['Subject 3A', 'Subject 3B', 'Subject 3C'],
  };

  const handleSubjectClick = (subject) => {
    setSelectedSubjectName(subject);
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    setSelectedFile(file);
  };

  return (
    <Container>
      <LeftContainer>
        <h2>Faculties</h2>
        <FacultyTree>
          <FacultyNode>
            <FacultyButton onClick={() => handleFacultyClick('Faculty 1')}>Faculty 1</FacultyButton>
            {showSubjects && selectedFaculty === 'Faculty 1' && (
              <SubjectTree>
                {subjects['Faculty 1'].map((subject) => (
                  <SubjectNode key={subject}>
                    <SubjectButton onClick={() => handleSubjectClick(subject)}>
                      {subject}
                    </SubjectButton>
                  </SubjectNode>
                ))}
              </SubjectTree>
            )}
          </FacultyNode>
          <FacultyNode>
            <FacultyButton onClick={() => handleFacultyClick('Faculty 2')}>Faculty 2</FacultyButton>
            {showSubjects && selectedFaculty === 'Faculty 2' && (
              <SubjectTree>
                {subjects['Faculty 2'].map((subject) => (
                  <SubjectNode key={subject}>
                    <SubjectButton onClick={() => handleSubjectClick(subject)}>
                      {subject}
                    </SubjectButton>
                  </SubjectNode>
                ))}
              </SubjectTree>
            )}
          </FacultyNode>
          <FacultyNode>
            <FacultyButton onClick={() => handleFacultyClick('Faculty 3')}>Faculty 3</FacultyButton>
            {showSubjects && selectedFaculty === 'Faculty 3' && (
              <SubjectTree>
                {subjects['Faculty 3'].map((subject) => (
                  <SubjectNode key={subject}>
                    <SubjectButton onClick={() => handleSubjectClick(subject)}>
                      {subject}
                    </SubjectButton>
                  </SubjectNode>
                ))}
              </SubjectTree>
            )}
          </FacultyNode>
        </FacultyTree>
      </LeftContainer>

      {selectedSubjectName && (
        <RightContainer>
          <h3>Selected Subject: {selectedSubjectName}</h3>
          {selectedFile ? (
            <p>File selected: {selectedFile.name}</p>
          ) : (
            <>
              <input
                type="file"
                style={{ display: 'none' }}
                id="file-upload"
                onChange={handleFileUpload}
              />
              <FileUploadButton htmlFor="file-upload">Upload File</FileUploadButton>
            </>
          )}
        </RightContainer>
      )}
    </Container>
  );
};

export default FacultyButtons;
