import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [link, setLink] = useState('');
  const [uploadChoice, setUploadChoice] = useState('document');
  const [documentFile, setDocumentFile] = useState(null);
  const [linkPhoto, setLinkPhoto] = useState(null);
  const [uploadedItem, setUploadedItem] = useState(null); // Store uploaded item

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleUploadChoiceChange = (choice) => {
    setUploadChoice(choice);
  };

  const handleDocumentFileChange = (e) => {
    setDocumentFile(e.target.files[0]);
  };

  const handleLinkPhotoChange = (e) => {
    setLinkPhoto(e.target.files[0]);
  };

  const handleUpload = () => {
    if (uploadChoice === 'document' && title && author && documentFile && linkPhoto) {
      // Handle document upload here, for example, by displaying the uploaded item
      const uploadedDocument = {
        type: 'document',
        title,
        author,
        documentFile,
        linkPhoto,
      };
      setUploadedItem(uploadedDocument);
    } else if (uploadChoice === 'link' && link && linkPhoto) {
      // Handle link upload here, for example, by displaying the uploaded item
      const uploadedLink = {
        type: 'link',
        link,
        linkPhoto,
      };
      setUploadedItem(uploadedLink);
    } else {
      alert('Please fill in all required fields.');
    }

    closeModal(); // Close the modal after upload
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        {/* Modal content */}
        <h2>Upload Document or Link</h2>
        <div>
          {/* Upload form */}
          {/* ... (existing upload form) */}
        </div>
        {/* Upload button */}
        <button
          onClick={handleUpload}
          style={{
            backgroundColor: '#8b0000',
            color: '#fff',
            border: 'none',
            borderRadius: '6px',
            padding: '0.6rem 1.2rem',
            fontSize: '1rem',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Upload
        </button>
      </Modal>

      {/* Display uploaded item */}
      {uploadedItem && (
        <div>
          <h3>Uploaded Item:</h3>
          {uploadedItem.type === 'document' ? (
            <div>
              <p>Title: {uploadedItem.title}</p>
              <p>Author: {uploadedItem.author}</p>
              <p>Type: Document</p>
              <p>Download Link: {/* Add the download link for the document */}</p>
            </div>
          ) : (
            <div>
              <p>Link: {uploadedItem.link}</p>
              <p>Type: Link</p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SubjectPage;
