import React, { useState } from 'react';
import Modal from './Modal';

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState(''); // Add title state
  const [author, setAuthor] = useState(''); // Add author state

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  // Handle input changes for title and author
  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  // Handle file upload
  const handleUpload = () => {
    // Implement the upload logic here using title, author, and selected file
    if (title && author) {
      // You can use title, author, and the selected file for the upload
      alert('File uploaded successfully.');
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div>
      <h1>Subject Page</h1>
      <button onClick={openModal} style={{ backgroundColor: '#8b0000', color: '#fff', border: 'none', borderRadius: '6px', padding: '0.6rem 1.2rem', fontSize: '1rem', cursor: 'pointer', marginTop: '10px' }}>
  Upload
</button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document</h2>
        <label>Title:</label>
        <input type="text" value={title} onChange={handleTitleChange} />
        <br />
        <label>Author:</label>
        <input type="text" value={author} onChange={handleAuthorChange} />
        <br />
        <label>Choose File:</label>
        <input type="file" />
        <br />
        <button onClick={handleUpload}>Upload</button>
      </Modal>
    </div>
  );
};

export default SubjectPage;
