import React, { useState } from 'react';
import styled from 'styled-components';

const StyledButton = styled.button`
  /* Your button styling */
`;

function FileUpload({ subject }) {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = () => {
    // Implement the upload logic here for the specific subject (use the subject prop)
    if (selectedFile) {
      // You can use the selectedFile and subject for the upload
      alert(`File uploaded successfully for ${subject}.`);
    }
  };

  return (
    <div>
      <h2>Upload Files for {subject}</h2>
      <input type="file" onChange={handleFileChange} />
      <StyledButton onClick={handleUpload}>Upload</StyledButton>
    </div>
  );
}

export default FileUpload;
