import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [link, setLink] = useState('');
  const [uploadChoice, setUploadChoice] = useState('document');
  const [uploads, setUploads] = useState([]); // Store uploaded items

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  const handleLinkChange = (e) => {
    setLink(e.target.value);
  };

  const handleUploadChoiceChange = (choice) => {
    setUploadChoice(choice);
  };

  const handleUpload = () => {
    if (uploadChoice === 'document' && title && author) {
      // Handle document upload
      const newUpload = {
        type: 'document',
        title,
        author,
      };
      setUploads([...uploads, newUpload]);
      alert('Document uploaded successfully.');
    } else if (uploadChoice === 'link' && link) {
      // Handle link upload
      const newUpload = {
        type: 'link',
        link,
      };
      setUploads([...uploads, newUpload]);
      alert('Link uploaded successfully.');
    } else {
      alert('Please fill in all required fields.');
    }
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <button
        onClick={openModal}
        style={{
          backgroundColor: '#8b0000',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          padding: '0.6rem 1.2rem',
          fontSize: '1rem',
          cursor: 'pointer',
          marginTop: '10px',
        }}
      >
        Upload
      </button>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document or Link</h2>
        {/* Rest of the modal content */}
      </Modal>

      {/* Display uploaded items */}
      {uploads.map((upload, index) => (
        <div key={index}>
          {upload.type === 'document' && (
            <div>
              <h3>{upload.title}</h3>
              <p>Author: {upload.author}</p>
              {/* Display image */}
              <img src={upload.photo} alt={upload.title} />
              {/* Add a download button */}
              <button
                onClick={() => {
                  // Implement the download functionality here
                  alert('Downloading document...');
                }}
              >
                Download
              </button>
            </div>
          )}
          {upload.type === 'link' && (
            <div>
              <p>Link: {upload.link}</p>
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default SubjectPage;
