import React, { useState } from 'react';
import styled from 'styled-components';

const FacultyButtonsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 2rem;
`;

const FacultyButton = styled.button`
  padding: 0.75rem 1.5rem;
  font-size: 1rem;
  background-color: #333;
  color: #fff;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  ${(props) => props.isSelected && `
    background-color: #666;
  `}
`;

const FacultyButtons = () => {
  const facultyTitles = ['FACULTY OF IT', 'FACULTY OF BUSSINESS', 'FACULTY OF PHARMACEY','FACULTY OF LAW','FACULTY OF MEDIA','FACULTY OF ARCHETCUTRE AND DESGIN'];

  const [selectedFaculty, setSelectedFaculty] = useState(null);

  const handleFacultyButtonClick = (index) => {
    setSelectedFaculty(index === selectedFaculty ? null : index);
  };

  return (
    <FacultyButtonsContainer>
      {facultyTitles.map((facultyTitle, index) => (
        <FacultyButton
          key={facultyTitle}
          onClick={() => handleFacultyButtonClick(index)}
          isSelected={selectedFaculty === index}
        >
          {facultyTitle}
        </FacultyButton>
      ))}
    </FacultyButtonsContainer>
  );
};

export default FacultyButtons;
