import React, { useState } from 'react';
import Modal from './Modal';
import { useParams } from 'react-router-dom';
import styled from 'styled-components'; // Import styled

const ChooseFileButtonModal = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000;
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-top: 10px;

  &:hover {
    background-color: #6a0000;
  }
`;

const SubjectPage = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');

  const { subjectName } = useParams();

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleAuthorChange = (e) => {
    setAuthor(e.target.value);
  };

  const handleUpload = () => {
    if (title && author) {
      alert('File uploaded successfully.');
    } else {
      alert('Please fill in all fields.');
    }
  };

  return (
    <div>
      <h1>{subjectName}</h1>
      <ChooseFileButtonModal onClick={openModal}>Choose File</ChooseFileButtonModal>

      <Modal isOpen={isModalOpen} onClose={closeModal}>
        <h2>Upload Document</h2>
        <label>Title:</label>
        <input type="text" value={title} onChange={handleTitleChange} />
        <br />
        <label>Author:</label>
        <input type="text" value={author} onChange={handleAuthorChange} />
        <br />
        <label>Choose File:</label>
        <input type="file" />
        <br />
        <ChooseFileButtonModal onClick={handleUpload}>Upload</ChooseFileButtonModal>
      </Modal>
    </div>
  );
};

export default SubjectPage;
