import React, { useState } from 'react';
import styled from 'styled-components';
import { faculties } from './data'; 

const Container = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  margin: 0 20px; /* Add margin on the left and right */
`;



const FacultyButton = styled.button`
  padding: 0.8rem 1.5rem;
  font-size: 1.2rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  margin-right: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const SubjectButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  margin-bottom: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
`;

const FileUploadButton = styled.button`
  padding: 0.6rem 1.2rem;
  font-size: 1rem;
  background-color: #8b0000; /* Dark red color */
  color: #fff;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  margin-right: 10px;
  margin-bottom: 10px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #6a0000; /* Slightly darker shade of red when hovered */
  }
  display: ${(props) => (props.show ? 'block' : 'none')};
`;


const FacultyButtons = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [selectedSubject, setSelectedSubject] = useState(null);

  const faculties = [
    { id: 1, name: 'Faculty Of IT' },
    { id: 2, name: 'Faculty Of Business' },
    { id: 3, name: 'Faculty Of Media' },
    { id: 4, name: 'Faculty Of Law' },
    { id: 5, name: 'Faculty Of Architecture' },
    { id: 6, name: 'Faculty Of Pharmacy' },
    { id: 7, name: 'Faculty Of Engineering' },
    { id: 8, name: 'Faculty Of Arts' },
    { id:9 , name: 'Faculty Of Medicene'}
  ];

  const subjects = [
    { id: 1, facultyId: 1, name: 'Subject 1' },
    { id: 2, facultyId: 1, name: 'Subject 2' },
    { id: 3, facultyId: 2, name: 'Subject 3' },
    { id: 4, facultyId: 2, name: 'Subject 4' },
    { id: 5, facultyId: 3, name: 'Subject 5' },
    { id: 6, facultyId: 3, name: 'Subject 6' },
    { id: 7, facultyId: 4, name: 'Subject 7' },
    { id: 8, facultyId: 4, name: 'Subject 8' },
    { id: 9, facultyId: 5, name: 'Subject 9' },
    { id: 10, facultyId: 5, name: 'Subject 10' },
    { id: 11, facultyId: 6, name: 'Subject 11' },
    { id: 12, facultyId: 6, name: 'Subject 12' },
    { id: 13, facultyId: 7, name: 'Subject 13' },
    { id: 14, facultyId: 7, name: 'Subject 14' },
    { id: 13, facultyId: 8, name: 'Subject 13' },
    { id: 13, facultyId: 9, name: 'Subject 13' }

  ];

  const handleFacultyClick = (facultyId) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
  };

  const handleSubjectClick = (subjectId) => {
    setSelectedSubject((prevSelectedSubject) =>
      prevSelectedSubject === subjectId ? null : subjectId
    );
  };

  return (
    <Container>
      {faculties.map((faculty) => (
        <div key={faculty.id}>
          <FacultyButton onClick={() => handleFacultyClick(faculty.id)}>
            {faculty.name}
          </FacultyButton>
          {selectedFaculty === faculty.id && (
            <div>
              {subjects
                .filter((subject) => subject.facultyId === faculty.id)
                .map((subject) => (
                  <React.Fragment key={subject.id}>
                    <SubjectButton onClick={() => handleSubjectClick(subject.id)}>
                      {subject.name}
                    </SubjectButton>
                    <FileUploadButton show={selectedSubject === subject.id}>
                      Upload File
                    </FileUploadButton>
                  </React.Fragment>
                ))}
            </div>
          )}
        </div>
      ))}
    </Container>
  );
};

export default FacultyButtons;
