import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Header from './Header';
import Home from './Home';
import FacultyButtons from './FacultyButtons';
import WelcomingPage from './WelcomingPage';
import Footer from './Footer';
import SubjectPage from './SubjectPage'; // Import the SubjectPage component
import Layout from './Layout'; 

const App = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [searchText, setSearchText] = useState('');
  const [showScrollButton, setShowScrollButton] = useState(false);
  const [showFaculties, setShowFaculties] = useState(false);

  const handleFacultyChange = (facultyId, facultyName) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
    setSearchText('');
  };

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  const handleScroll = () => {
    const isScrolledDown = window.scrollY > 200;
    setShowScrollButton(isScrolledDown);
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <Layout>
            <Home />
            {showFaculties && (
              <div id="faculty-section">
                <FacultyButtons
                  selectedFaculty={selectedFaculty}
                  setSelectedFaculty={handleFacultyChange}
                />
              </div>
            )}
          </Layout>
        </Route>
        {/* Add a new route for the SubjectPage */}
        <Route
          path="/subjects/:subjectName"
          render={({ match }) => (
            <Layout>
              <SubjectPage selectedSubject={match.params.subjectName} />
            </Layout>
          )}
        />
      </Switch>
    </Router>
  );
};


export default App;
