import React, { useState } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Header from './Header'; // Make sure the path is correct
import Home from './Home'; // Make sure the path is correct
import Login from './Login'; // Make sure the path is correct
import SignUp from './SignUp'; // Make sure the path is correct
import FacultyButtons from './FacultyButtons'; // Import the FacultyButtons component

const App = () => {
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [searchText, setSearchText] = useState('');

  const handleFacultyChange = (facultyId, facultyName) => {
    setSelectedFaculty((prevSelectedFaculty) =>
      prevSelectedFaculty === facultyId ? null : facultyId
    );
    setSearchText('');
  };

  const handleSearchChange = (event) => {
    setSearchText(event.target.value);
  };

  return (
    <Router>
      <Header
        selectedFacultyName={selectedFaculty ? `Faculty ${selectedFaculty}` : 'All Faculties'}
        onSearchChange={handleSearchChange}
      />
      <Switch>
        <Route exact path="/" component={Home} />
        <Route path="/login" component={Login} />
        <Route path="/signup" component={SignUp} />
      </Switch>
      {/* Pass selectedFaculty and setSearchText props to FacultyButtons */}
      <FacultyButtons selectedFaculty={selectedFaculty} setSelectedFaculty={handleFacultyChange} />
    </Router>
  );
};

export default App;
